var text = <Navigation />;
export const Play = (props)=>{
    console.log(socket);
    socket.on("connect", () => {
        console.log("hello");
        text = (<><Navigation /><Chat socket={socket} /></>);
        console.log(text);
    });
    return (<>{text}</>)
    
