import React, {useState} from 'react';
import { io } from "socket.io-client";
import { CardSelection } from '../components/cardSelection';
import { WaitRoom } from '../components/waitRoom';
import { Game } from '../../Game/containers/game';

const socket = io('http://localhost:8085');



export const Play = (props)=>{
    const [view,setView] = useState("cardSelection");
    const [user1,setUser1]= useState(null);
    const [user2,setUser2]= useState(null);
    socket.on("connect", () => {
    });

    
    function startGameHandle(){
        setView("wait");
        socket.emit("newPlayerIsWaiting",props.user);        
        socket.on("launchGame",function(player1, player2){
            setUser1(player1);
            setUser2(player2);
        });
    }
    let render;
    switch(view){
        case "cardSelection":
            render = (<CardSelection card={props.user.cardList} startGameHandle={startGameHandle}></CardSelection>)
            break;
        case "wait":
            render = (<WaitRoom />)
            break;
        case "game":
            render = (<Game user={props.user} socket={socket} player1={user1} player2={user2}/>)
            break;
        default:
            console.log("Error: this view doesn't exist !");
    }

    return (<>{render}</>)
    
  }
