import React, { useEffect, useState } from 'react';

import ChatBar from './components/ChatBar';

import ChatBody from './components/ChatBody';

import ChatFooter from './components/ChatFooter';


export const Chat = (props) => {
  if(props.socket!=null){
    const [messages, setMessages] = useState([]);
    const [userName, setUserName] = useState("Fred");

    
    props.socket.emit('newUser', {userName: userName, socketID: props.socket.id });
    
    


    useEffect(() => {

      props.socket.on('messageResponse', (data) => setMessages([...messages, data]));

    }, [props.socket, messages]);
  }

  return (

    <div className="chat">

      <ChatBar socket={props.socket} />

      <div className="chat__main">

        <ChatBody messages={messages} />

        <ChatFooter socket={props.socket} />

      </div>

    </div>

  );

};
