import React, {Component,useState} from 'react';
import { connect } from 'react-redux';
import { Chat } from '../../Chat/chat';
import { io } from "socket.io-client";
import { Navigation } from '../../Navigation/navigation';

const socket = io('http://localhost:8085');
var view = "cardSelection";

export const Play = (props)=>{
    const [text, setText] = useState(<Navigation />);
    socket.on("connect", () => {
        setText(<><Navigation /><Chat socket={socket} /></>);
    });
    if (props.view === undefined){
    }else{
        view = props.view;
    }

    switch(view){
        case "cardSelection":
            render = (<CardSelection card={this.props.user.cardList} startGameHandler={this.startGameHandler}></CardSelection>)
            break;
        case "wait":
            render = (<WaitRoom />)
            break;
        case "game":
            render = (<Game user={this.props.user} socket={socket} player1={this.state.player1} player2={this.state.player2}/>)
            break;
        default:
            console.log("Error: this view doesn't exist !");
    }

    return (<>{text}</>)
    
  }
