    socket.on('newUserResponse', (data) => {
      if(data.userName!=props.user.username){
        setUser(data);
      }
      
    })
  }, [socket, user]);


  return (

    <div className="chat__sidebar">

      <h2>Open Chat</h2>

      <div>

        <h4 className="chat__header">ACTIVE USERS</h4>

