const socket = io('http://localhost:8083');

export const Play = (props)=>{
    const [text, setText] = useState(<Navigation />);
    socket.on("connect", () => {
        setText(<><Navigation /><Chat socket={socket} /></>);
    });
    return (<>{text}</>)
    
